/* Block commonly abused remote access tools */
event.type = "Process Creation" AND ( src.process.name contains:anycase "splashtop" OR src.process.name contains:anycase "screenconnect" OR src.process.name contains:anycase "anydesk" OR src.process.name contains:anycase "logmein" OR src.process.name contains:anycase "gotomypc" OR src.process.name contains:anycase "dwrcc" OR src.process.name contains:anycase "rustdesk" OR src.process.name contains:anycase "nxserver" OR src.process.name contains:anycase "zaservice" OR src.process.name contains:anycase "thincast" ) AND NOT ( src.process.name contains:anycase "teamviewer" OR src.process.name contains:anycase "pdqconnect" OR src.process.name contains:anycase "pdqagent" )